            var showGreeting = function() {
                var now = new Date();

                var hh = now.getHours();

                var greeting;
                if (5 <= hh && hh < 12) {
                    greeting = 'Good morning';
                } else if (12 <= hh && hh < 16) {
                    greeting = 'Good afternoon';
                } else {
                    greeting = 'Good evening';
                }

                hh = hh < 10 ? '0' + hh : hh;
                var mm = now.getMinutes();
                mm = mm < 10 ? '0' + mm : mm;

                $('#txtClock').text(hh + ':' + mm);
                $('#txtGreeting').text(greeting);
            };

            showGreeting();
            setTimeout(showGreeting, 60000);

        });

