        var refreshTokensManager = {
            refreshTimeout: 1080000,
            setLocalStorageVars: function(date) {
                if (!date || !(date instanceof Date)) {
                    date = new Date();
                }
                refreshTokensManager.setLastActionDate(date);
                refreshTokensManager.setRefreshTimerStartDate(date);
            },
            setLastActionDate: function(date) {
                date instanceof Date && localStorage.setItem('LAST_ACTION_DATE', date)
            },
            setRefreshTimerStartDate: function(date) {
                date instanceof Date && localStorage.setItem('REFRESH_TIMER_START', date)
            },
            getLastActionDate: function() {
                return Date.parse(localStorage.getItem('LAST_ACTION_DATE'))
            },
            getRefreshTimerStartDate: function() {
                return Date.parse(localStorage.getItem('REFRESH_TIMER_START'))
            },
            startRefreshTimer: function(timeToRun) {
                setTimeout(function() {
                        // Check if action in last 18 minutes
                        var lastActionDate = refreshTokensManager.getLastActionDate();
                        var timerStart = refreshTokensManager.getRefreshTimerStartDate();
                        if (lastActionDate > timerStart) {
                            // Refresh Token
                            refreshTokensManager.refreshST()
                                .then(function() {
                                    refreshTokensManager.refreshAT();
                                    // set local storage vars
                                    refreshTokensManager.setLocalStorageVars(new Date());
                                    refreshTokensManager.startRefreshTimer(refreshTokensManager.refreshTimeout);
                                })
                                .catch(function() {});
                        }
                    },
                    timeToRun);
            },
            refreshST: function() {
                return new Promise(function(resolve, reject) {
                    $.ajax({
                            type: 'GET',
                            url: 'https://login.payoneer.com:443/api/v2/internal/authorize?client_id=b3d186db-4e5d-49c8-8a12-5753136af807&redirect_uri=https%3a%2f%2fmyaccount.brand.domain%2flogin%2flogin.aspx&scope=myaccount openid&response_type=code&state=1b8b18ca-1fbf-4830-9e7b-19dde4101484&prompt=none',
                            xhrFields: {
                                withCredentials: true
                            }
                        })
                        .done(resolve)
                        .fail(reject);
                });
            },
            refreshAT: function() {

                var tmpData = {};
                $.extend(tmpData, {
                    'PayoneerInternalId': $('#qaz').val()
                });
                $.ajax({
                    type: 'POST',
                    url: 'SetAccount.aspx/KeepAlive',
                    contentType: 'application/json; charset=utf-8',
                    data: JSON.stringify(tmpData),
                    success: function(payload) {
                        var res = JSON.parse(payload.d);
                        var authObj = buildAuthObj(res.AT, res.expiresAt);
                        authObj['expiresAt'] = addSecondsToDateNowAndStringify(authObj['expiresAt']);
                        if (res.isOk && res.AT) {
                            setAuthObjInSession(authObj);
                        }
                    }
                });
            }
        };

        (function() {

            var authObj = buildAuthObj('AAIkYjNkMTg2ZGItNGU1ZC00OWM4LThhMTItNTc1MzEzNmFmODA3SMzcyhWhnVTxdkQ8bekSs-a0Et5qFStrUqtcICceyXgOalX8_lcx62P4y4EZgDUYUuoKPzYjWiceYSSIaW1HYvxRpidfVpoZrpYDy5Z-jAy4zSIPt2hsCkO6lgge6kVPv0klsbPJcs-TUUmbBbViyao4xcImGK3O3ddzeki9mLvtNV4s75Gnm-mblJadzf4E', '1200');
            authObj['expiresAt'] = addSecondsToDateNowAndStringify(authObj['expiresAt']);
            var currentObj = sessionStorage.getItem('ui-legacy-auth');
            if (currentObj !== authObj) {
                setAuthObjInSession(authObj);
            }
        })();

        function addSecondsToDateNowAndStringify(expiresAt) {
            var now = new Date();
            now.setSeconds(now.getSeconds() + parseInt(expiresAt));
            return now.toISOString();
        }

        function setAuthObjInSession(authObj) {
            sessionStorage.setItem('ui-legacy-auth', JSON.stringify(authObj));
        }

        function buildAuthObj(at, exp) {
            var authObj = {};
            authObj.AT = at;
            authObj.expiresAt = exp;
            return authObj;
        }

        function getMyAccountAccessToken() {
            var authObj = JSON.parse(sessionStorage.getItem('ui-legacy-auth'));
            if ('undefined' === typeof(authObj['AT'])) {
                return null;
            }
            return authObj.AT;
        }

        (function() {

            var isLogin = false;
            // Set payoneerAjax to be oldAjax
            $.oldAjax = $.payoneerAjax;

            // Define payoneerAjax that will set "last action" and will call the original payoneerAjax (now oldAjax)
            $.payoneerAjax = function() {
                refreshTokensManager.setLastActionDate(new Date());
                return $.oldAjax.apply(this, arguments);
            }

            // Copy all oldAjax fields to payoneerAjax - this is because the original function needs her own fields and it knows them only by the original name (payoneerAjax)
            for (var k in $.oldAjax) {
                $.payoneerAjax[k] = $.oldAjax[k];
            }

            var currentTime = new Date();
            if (isLogin ||
                !refreshTokensManager.getLastActionDate() ||
                !refreshTokensManager.getRefreshTimerStartDate() ||
                refreshTokensManager.getLastActionDate() >= Date.now() ||
                refreshTokensManager.getRefreshTimerStartDate() >= Date.now()) {
                // Set the last action date and the timer start time
                refreshTokensManager.setLocalStorageVars(currentTime);
                refreshTokensManager.startRefreshTimer(refreshTokensManager.refreshTimeout);
            } else {
                // Set the timeout for the refresh SSO token call
                refreshTokensManager.setLastActionDate(currentTime);
                var timerStart = refreshTokensManager.getRefreshTimerStartDate();
                var timerEnd = timerStart + refreshTokensManager.refreshTimeout;
                var timeLeft = timerEnd - Date.parse(currentTime);
                if (timeLeft > 0) {
                    refreshTokensManager.startRefreshTimer(timeLeft);
                }
            }
        })();

        var p_custdata = {
            "customerId": "30404981",
            "chType": 1,
